presenters/action.content:
(function() {
  module.exports = function(action, editor) {
    var hotkey, icon, name;
    hotkey = action.hotkey, icon = action.icon, name = action.name;
    return {
      perform: function(e) {
        e.preventDefault();
        return editor.dispatchAction(action);
      },
      title: function() {
        return "" + name + " [" + hotkey + "]";
      },
      name: function() {
        return name;
      },
      style: function() {
        return "background-image: url(" + icon + ")";
      }
    };
  };

}).call(this);

presenters/color.content:
(function() {
  module.exports = function(color, editor) {
    var showPicker;
    showPicker = false;
    return {
      color: color,
      click: function(e) {
        e.target.blur();
        if (!showPicker) {
          showPicker = true;
          e.preventDefault();
        }
        setTimeout(function() {
          return showPicker = false;
        }, 500);
        editor.activeColor(color());
      },
      input: function(e) {
        return editor.activeColor(e.target.value);
      },
      style: function() {
        var c;
        c = color();
        return "background-color: " + c + "; color: " + c + ";";
      }
    };
  };

}).call(this);

presenters/opacity.content:
(function() {
  module.exports = function(editor) {
    return {
      focus: function() {
        return editor.detailTool("opacity");
      },
      value: editor.alpha,
      detailClass: function() {
        if ("opacity" === editor.detailTool()) {
          return "panelOpen";
        }
      },
      min: 0,
      max: 100,
      step: 1
    };
  };

}).call(this);

presenters/tool.content:
(function() {
  module.exports = function(editor, tool) {
    var self;
    return self = {
      activate: function() {
        return editor.activeTool(tool);
      },
      activeClass: function() {
        if (tool === editor.activeTool()) {
          return "active";
        }
      },
      detail: function() {
        return editor.detailTool(tool);
      },
      detailClass: function() {
        if (tool === editor.detailTool()) {
          return "panelOpen";
        }
      },
      style: function() {
        return "background-image: url(" + tool.iconUrl + ")";
      },
      title: function() {
        return tool.hotkeys;
      },
      settings: tool.settings
    };
  };

}).call(this);

