(function() {
  if (!("path" in Event.prototype)) {
    Object.defineProperty(Event.prototype, "path", {
      get: function() {
        var node, path;
        path = [];
        node = this.target;
        while (node) {
          path.push(node);
          node = node.parentNode;
        }
        return path;
      }
    });
  }

}).call(this);

stripe-payment.content:
(function() {
  var amount, description, process, push, q;

  q = [];

  push = function(selector) {
    return q.push(selector);
  };

  $.getScript('https://checkout.stripe.com/checkout.js').done(function() {
    console.log("loaded");
    q.forEach(process);
    return push = process;
  });

  description = 'Pixi Paint ($1.99)';

  amount = 199;

  process = function(selector) {
    var handler;
    handler = StripeCheckout.configure({
      key: 'pk_PPCRZgLrovwFHSKmMkjtVONHDs3pR',
      image: 'https://fbcdn-photos-h-a.akamaihd.net/hphotos-ak-xap1/t39.2081-0/851574_391517107646989_794757491_n.png',
      token: function(token, args) {
        return $.post("http://pixi-paint-payments.herokuapp.com/charge", {
          amount: amount,
          description: description,
          stripeEmail: token.email,
          stripeToken: token.id
        });
      }
    });
    return document.querySelector(selector).addEventListener('click', function(e) {
      handler.open({
        name: 'Pixi Paint',
        description: description,
        amount: amount
      });
      return e.preventDefault();
    });
  };

  module.exports = function(selector) {
    return push(selector);
  };

}).call(this);

