      var p1, p2, result;
      p1 = Point(5, 6);
      p2 = Point(7, 5);
      result = p1.add(p2);
      equals(result.x, p1.x + p2.x);
      equals(result.y, p1.y + p2.y);
      equals(p1.x, 5);
      equals(p1.y, 6);
      equals(p2.x, 7);
      return equals(p2.y, 5);
    });
    it("#add with two arguments", function() {
      var point, result, x, y;
      point = Point(3, 7);
      x = 2;
      y = 1;
      result = point.add(x, y);
      equals(result.x, point.x + x);
      equals(result.y, point.y + y);
      x = 2;
      y = 0;
      result = point.add(x, y);
      equals(result.x, point.x + x);
      return equals(result.y, point.y + y);
    });
    it("#add existing", function() {
      var p;
      p = Point(0, 0);
      p.add(Point(3, 5));
      equals(p.x, 0);
      return equals(p.y, 0);
    });
    it("#subtract", function() {
      var p1, p2, result;
      p1 = Point(5, 6);
      p2 = Point(7, 5);
      result = p1.subtract(p2);
      equals(result.x, p1.x - p2.x);
      return equals(result.y, p1.y - p2.y);
    });
    it("#subtract existing", function() {
      var p;
      p = Point(8, 6);
      p.subtract(3, 4);
      equals(p.x, 8);
      return equals(p.y, 6);
    });
    it("#norm", function() {
      var normal, p;
      p = Point(2, 0);
      normal = p.norm();
      equals(normal.x, 1);
      normal = p.norm(5);
      equals(normal.x, 5);
      p = Point(0, 0);
      normal = p.norm();
      equals(normal.x, 0, "x value of norm of point(0,0) is 0");
      return equals(normal.y, 0, "y value of norm of point(0,0) is 0");
    });
    it("#norm existing", function() {
      var p;
      p = Point(6, 8);
      p.norm(5);
      equals(p.x, 6);
      return equals(p.y, 8);
    });
