    var handle, handlers;
    handlers = {};
    return handle = function(event) {
      var combo, key, modifiersActive;
      key = event.key;
      modifiersActive = ["alt", "ctrl", "meta", "shift"].filter(function(modifier) {
        return event["" + modifier + "Key"];
      });
      combo = modifiersActive.concat(key).join("+");
      return typeof handlers[combo] === "function" ? handlers[combo](e) : void 0;
    };
  };

}).call(this);

