(function() {
  var CommandStack;

  CommandStack = require("command-stack");

  module.exports = function(I, self) {
    var commandStack;
    if (I == null) {
      I = {};
    }
    if (self == null) {
      self = Core(I);
    }
    commandStack = CommandStack();
    self.extend({
      history: function(newHistory) {
        if (newHistory == null) {
          newHistory = [];
        }
        if (arguments.length > 0) {
          return commandStack = CommandStack(newHistory);
        } else {
          return commandStack.stack();
        }
      },
      execute: function(command) {
        commandStack.execute(command);
        return self;
      },
      undo: function() {
        commandStack.undo();
        return self;
      },
      redo: function() {
        commandStack.redo();
        return self;
      }
    });
    return self;
  };

}).call(this);

