(function() {
  var CommandStack;

  CommandStack = function(stack) {
    var index;
    if (stack == null) {
      stack = [];
    }
    index = stack.length;
    return {
      execute: function(command) {
        stack[index] = command;
        command.execute();
        index += 1;
        stack.length = index;
        return this;
      },
      undo: function() {
        var command;
        if (this.canUndo()) {
          index -= 1;
          command = stack[index];
          command.undo();
          return command;
        }
      },
      redo: function() {
        var command;
        if (this.canRedo()) {
          command = stack[index];
          command.execute();
          index += 1;
          return command;
        }
      },
      current: function() {
        return stack[index - 1];
      },
      canUndo: function() {
        return index > 0;
      },
      canRedo: function() {
        return stack[index] != null;
      },
      stack: function() {
        return stack.slice(0, index);
      }
    };
  };

  module.exports = CommandStack;

}).call(this);

