test/command_stack.content:
(function() {
  var CommandStack, equals, ok;

  CommandStack = require("../main");

  ok = assert;

  equals = assert.equal;

  describe("CommandStack", function() {
    it("undo on an empty stack returns undefined", function() {
      var commandStack;
      commandStack = CommandStack();
      return equals(commandStack.undo(), void 0);
    });
    it("redo on an empty stack returns undefined", function() {
      var commandStack;
      commandStack = CommandStack();
      return equals(commandStack.redo(), void 0);
    });
    it("executes commands", function() {
      var command, commandStack;
      command = {
        execute: function() {
          return ok(true, "command executed");
        }
      };
      commandStack = CommandStack();
      return commandStack.execute(command);
    });
    it("can undo", function() {
      var command, commandStack;
      command = {
        execute: function() {},
        undo: function() {
          return ok(true, "command executed");
        }
      };
      commandStack = CommandStack();
      commandStack.execute(command);
      return commandStack.undo();
    });
    it("can redo", function() {
      var command, commandStack;
      command = {
        execute: function() {
          return ok(true, "command executed");
        },
        undo: function() {}
      };
      commandStack = CommandStack();
      commandStack.execute(command);
      commandStack.undo();
      return commandStack.redo();
    });
    it("executes redone command once on redo", function() {
      var command, commandStack;
      command = {
        execute: function() {
          return ok(true, "command executed");
        },
        undo: function() {}
      };
      commandStack = CommandStack();
      commandStack.execute(command);
      commandStack.undo();
      commandStack.redo();
      equals(commandStack.redo(), void 0);
      return equals(commandStack.redo(), void 0);
    });
    it("command is returned when undone", function() {
      var command, commandStack;
      command = {
        execute: function() {},
        undo: function() {}
      };
      commandStack = CommandStack();
      commandStack.execute(command);
      return equals(commandStack.undo(), command, "Undone command is returned");
    });
    it("command is returned when redone", function() {
      var command, commandStack;
      command = {
        execute: function() {},
        undo: function() {}
      };
      commandStack = CommandStack();
      commandStack.execute(command);
      commandStack.undo();
      return equals(commandStack.redo(), command, "Redone command is returned");
    });
    return it("cannot redo an obsolete future", function() {
      var Command, commandStack;
      Command = function() {
        return {
          execute: function() {},
          undo: function() {}
        };
      };
      commandStack = CommandStack();
      commandStack.execute(Command());
      commandStack.execute(Command());
      commandStack.undo();
      commandStack.undo();
      equals(commandStack.canRedo(), true);
      commandStack.execute(Command());
      return equals(commandStack.canRedo(), false);
    });
  });

}).call(this);

